import React, { useState } from "react";

function LoanOfferComparison() {
  // form state
  const [firstName, setFirstName] = useState("");
  const [emailAddress, setEmailAddress] = useState("");
  const [loanAmount, setLoanAmount] = useState<number | "">("");
  const [loanTerm, setLoanTerm] = useState<number | "">("");

  // error state
  const [errors, setErrors] = useState({
    firstName: "",
    emailAddress: "",
    loanAmount: "",
    loanTerm: "",
  });

  // validation function
  const validateField = (field: string, value: any) => {
    let message = "";

    switch (field) {
      case "firstName":
        if (!value.trim()) message = "First name is required.";
        break;

      case "emailAddress":
        if (!value.trim()) message = "Email is required.";
        else if (!/\S+@\S+\.\S+/.test(value))
          message = "Enter a valid email address.";
        break;

      case "loanAmount":
        if (value === "" || isNaN(value)) message = "Loan amount is required.";
        else if (value < 2000) message = "Loan amount must be at least $2,000.";
        break;

      case "loanTerm":
        if (value === "" || isNaN(value)) message = "Loan term is required.";
        else if (value < 1 || value > 7)
          message = "Loan term must be between 1 and 7 years.";
        break;

      default:
        break;
    }

    setErrors((prev) => ({ ...prev, [field]: message }));
  };

  // handle submit
  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();

    // final check
    if (
      Object.values(errors).some((err) => err) ||
      !firstName ||
      !emailAddress ||
      !loanAmount ||
      !loanTerm
    ) {
      alert("Please fix errors before submitting.");
      return;
    }

    console.log({ firstName, emailAddress, loanAmount, loanTerm });
    alert("Form submitted! (Check console for values)");
  };

  return (
    <div>
      <form onSubmit={handleSubmit}>
        {/* First Name */}
        <input
          type="text"
          placeholder="First Name"
          value={firstName}
          onChange={(e) => {
            setFirstName(e.target.value);
            validateField("firstName", e.target.value);
          }}
          onBlur={() => validateField("firstName", firstName)}
        />
        {errors.firstName && <p style={{ color: "red" }}>{errors.firstName}</p>}
        <br />

        {/* Email */}
        <input
          type="email"
          placeholder="Email Address"
          value={emailAddress}
          onChange={(e) => {
            setEmailAddress(e.target.value);
            validateField("emailAddress", e.target.value);
          }}
          onBlur={() => validateField("emailAddress", emailAddress)}
        />
        {errors.emailAddress && (
          <p style={{ color: "red" }}>{errors.emailAddress}</p>
        )}
        <br />

        {/* Loan Amount */}
        <input
          type="number"
          placeholder="Loan Amount (At least $2000)"
          value={loanAmount}
          onChange={(e) => {
            const val = e.target.value === "" ? "" : Number(e.target.value);
            setLoanAmount(val);
            validateField("loanAmount", val);
          }}
          onBlur={() => validateField("loanAmount", loanAmount)}
          min={2000}
        />
        {errors.loanAmount && (
          <p style={{ color: "red" }}>{errors.loanAmount}</p>
        )}
        <br />

        {/* Loan Term */}
        <input
          type="number"
          placeholder="Loan Term (1-7 Years)"
          value={loanTerm}
          onChange={(e) => {
            const val = e.target.value === "" ? "" : Number(e.target.value);
            setLoanTerm(val);
            validateField("loanTerm", val);
          }}
          onBlur={() => validateField("loanTerm", loanTerm)}
          min={1}
          max={7}
        />
        {errors.loanTerm && <p style={{ color: "red" }}>{errors.loanTerm}</p>}
        <br />

        {/* Submit Button */}
        <button
          type="submit"
          disabled={
            !firstName ||
            !emailAddress ||
            !loanAmount ||
            !loanTerm ||
            Object.values(errors).some((err) => err)
          }
        >
          Submit
        </button>
      </form>
    </div>
  );
}

export default LoanOfferComparison;
