import React, { useState } from "react";

function LoanOfferComparison() {
  const [firstName, setFirstName] = useState("");
  const [emailAddress, setEmailAddress] = useState("");
  const [loanAmount, setLoanAmount] = useState<number | "">("");
  const [loanTerm, setLoanTerm] = useState<number | "">("");

  // Track field errors
  const [errors, setErrors] = useState<{ [key: string]: string }>({});
  // Track touched fields
  const [touched, setTouched] = useState<{ [key: string]: boolean }>({});

  // Validation helper (checks all)
  const validateAllFields = () => {
    const newErrors: { [key: string]: string } = {};

    if (!firstName.trim()) newErrors.firstName = "First name is required.";
    if (!emailAddress) {
      newErrors.emailAddress = "Email address is required.";
    } else if (!/\S+@\S+\.\S+/.test(emailAddress)) {
      newErrors.emailAddress = "Enter a valid email.";
    }
    if (!loanAmount) {
      newErrors.loanAmount = "Loan amount is required.";
    } else if (typeof loanAmount === "number" && loanAmount < 2000) {
      newErrors.loanAmount = "Loan amount must be at least $2000.";
    }
    if (!loanTerm) {
      newErrors.loanTerm = "Loan term is required.";
    } else if (typeof loanTerm === "number" && (loanTerm < 1 || loanTerm > 7)) {
      newErrors.loanTerm = "Loan term must be between 1 and 7 years.";
    }

    return newErrors;
  };

  // Validate single field
  const validateField = (field: string, value: any) => {
    let error = "";

    if (field === "firstName" && !value.trim()) {
      error = "First name is required.";
    }
    if (field === "emailAddress") {
      if (!value) error = "Email address is required.";
      else if (!/\S+@\S+\.\S+/.test(value)) error = "Enter a valid email.";
    }
    if (field === "loanAmount") {
      if (!value) error = "Loan amount is required.";
      else if (typeof value === "number" && value < 2000)
        error = "Loan amount must be at least $2000.";
    }
    if (field === "loanTerm") {
      if (!value) error = "Loan term is required.";
      else if (typeof value === "number" && (value < 1 || value > 7))
        error = "Loan term must be between 1 and 7 years.";
    }

    return error;
  };

  const handleBlur = (field: string, value: any) => {
    setTouched({ ...touched, [field]: true });
    setErrors({ ...errors, [field]: validateField(field, value) });
  };

  const handleChange = (field: string, value: any) => {
    if (field === "firstName") setFirstName(value);
    if (field === "emailAddress") setEmailAddress(value);
    if (field === "loanAmount")
      setLoanAmount(value === "" ? "" : Number(value));
    if (field === "loanTerm") setLoanTerm(value === "" ? "" : Number(value));

    // live-validate only if already touched
    if (touched[field]) {
      setErrors({ ...errors, [field]: validateField(field, value) });
    }
  };

  // Reset form + errors
  const resetForm = () => {
    setFirstName("");
    setEmailAddress("");
    setLoanAmount("");
    setLoanTerm("");
    setErrors({});
    setTouched({});
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    const newErrors = validateAllFields();

    if (Object.keys(newErrors).length > 0) {
      setErrors(newErrors);
      setTouched({
        firstName: true,
        emailAddress: true,
        loanAmount: true,
        loanTerm: true,
      });
      return;
    }

    alert("Form submitted successfully!");
    resetForm();
  };

  const isFormValid =
    Object.values(errors).every((err) => !err) &&
    firstName &&
    emailAddress &&
    loanAmount &&
    loanTerm;

  return (
    <form onSubmit={handleSubmit}>
      {/* First Name */}
      <div style={{ display: "flex", alignItems: "center", gap: "10px" }}>
        <input
          type="text"
          placeholder="First Name"
          value={firstName}
          onChange={(e) => handleChange("firstName", e.target.value)}
          onBlur={(e) => handleBlur("firstName", e.target.value)}
        />
        {touched.firstName && errors.firstName && (
          <span style={{ color: "red" }}>{errors.firstName}</span>
        )}
      </div>

      {/* Email */}
      <div style={{ display: "flex", alignItems: "center", gap: "10px" }}>
        <input
          type="email"
          placeholder="Email Address"
          value={emailAddress}
          onChange={(e) => handleChange("emailAddress", e.target.value)}
          onBlur={(e) => handleBlur("emailAddress", e.target.value)}
        />
        {touched.emailAddress && errors.emailAddress && (
          <span style={{ color: "red" }}>{errors.emailAddress}</span>
        )}
      </div>

      {/* Loan Amount */}
      <div style={{ display: "flex", alignItems: "center", gap: "10px" }}>
        <input
          type="number"
          placeholder="Loan Amount"
          value={loanAmount}
          onChange={(e) => handleChange("loanAmount", e.target.value)}
          onBlur={(e) => handleBlur("loanAmount", e.target.value)}
        />
        {touched.loanAmount && errors.loanAmount && (
          <span style={{ color: "red" }}>{errors.loanAmount}</span>
        )}
      </div>

      {/* Loan Term */}
      <div style={{ display: "flex", alignItems: "center", gap: "10px" }}>
        <input
          type="number"
          placeholder="Loan Term (1-7 years)"
          value={loanTerm}
          onChange={(e) => handleChange("loanTerm", e.target.value)}
          onBlur={(e) => handleBlur("loanTerm", e.target.value)}
        />
        {touched.loanTerm && errors.loanTerm && (
          <span style={{ color: "red" }}>{errors.loanTerm}</span>
        )}
      </div>

      <button type="submit" disabled={!isFormValid}>
        Submit
      </button>
    </form>
  );
}

export default LoanOfferComparison;
