import React, { useState } from "react";

function LoanOfferComparison() {
  //form state
  const [firstName, setFirstName] = useState("");
  const [emailAddress, setEmailAddress] = useState("");
  const [loanAmount, setLoanAmount] = useState<number | "">("");
  const [loanTerm, setLoanTerm] = useState<number | "">("");

  //validation state
  const [errors, setErrors] = useState({
    firstName: "",
    emailAddress: "",
    loanAmount: "",
    loanTerm: "",
  });

  //handle submit
  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();

    let formErrors = {
      firstName: "",
      emailAddress: "",
      loanAmount: "",
      loanTerm: "",
    };

    if (!firstName) formErrors.firstName = "First name is required.";
    if (!emailAddress) formErrors.emailAddress = "Email is required.";
    if (loanAmount === "") {
      formErrors.loanAmount = "Loan amount is required.";
    } else if (loanAmount < 2000) {
      formErrors.loanAmount = "Loan amount must be at least $2,000.";
    }
    if (loanTerm === "") {
      formErrors.loanTerm = "Loan term is required.";
    } else if (loanTerm < 1 || loanTerm > 7) {
      formErrors.loanTerm = "Loan term must be between 1 and 7 years.";
    }

    setErrors(formErrors);

    //if any error exists, stop
    if (
      formErrors.firstName ||
      formErrors.emailAddress ||
      formErrors.loanAmount ||
      formErrors.loanTerm
    ) {
      return;
    }

    console.log({ firstName, emailAddress, loanAmount, loanTerm });
    alert("Form submitted! (Check console for values");
  };

  return (
    <div>
      <form onSubmit={handleSubmit}>
        {}
        <input
          type="text"
          placeholder="First Name"
          value={firstName}
          onChange={(e) => setFirstName(e.target.value)}
          required
        />
        {errors.firstName && <p style={{ color: "red" }}>{errors.firstName}</p>}
        <br />

        <input
          type="email"
          placeholder="Email Address"
          value={emailAddress}
          onChange={(e) => setEmailAddress(e.target.value)}
          required
        />
        {errors.emailAddress && (
          <p style={{ color: "red" }}>{errors.emailAddress}</p>
        )}
        <br />

        <input
          type="number"
          placeholder="Loan Amount (At least $2000)"
          value={loanAmount}
          onChange={(e) =>
            setLoanAmount(e.target.value === "" ? "" : Number(e.target.value))
          }
          required
          min={2000}
        />
        <br />

        <input
          type="number"
          placeholder="Loan Term (1-7 Years)"
          value={loanTerm}
          onChange={(e) =>
            setLoanTerm(e.target.value === "" ? "" : Number(e.target.value))
          }
          required
          min={1}
          max={7}
        />
        <br />

        <button
          type="submit"
          disabled={
            !firstName ||
            !emailAddress ||
            !loanAmount ||
            loanAmount < 2000 ||
            !loanTerm ||
            loanTerm < 1 ||
            loanTerm > 7
          }
        >
          Submit
        </button>
      </form>
    </div>
  );
}

export default LoanOfferComparison;
