import React, { useState } from "react";

function LoanOfferComparison() {
  const [firstName, setFirstName] = useState("");
  const [emailAddress, setEmailAddress] = useState("");
  const [loanAmount, setLoanAmount] = useState<number | "">("");
  const [loanTerm, setLoanTerm] = useState<number | "">("");
  const [errors, setErrors] = useState<{ [key: string]: string }>({});

  // Validation helper
  const validateAllFields = () => {
    const newErrors: { [key: string]: string } = {};

    if (!firstName.trim()) newErrors.firstName = "First name is required.";
    if (!emailAddress) {
      newErrors.emailAddress = "Email address is required.";
    } else if (!/\S+@\S+\.\S+/.test(emailAddress)) {
      newErrors.emailAddress = "Enter a valid email.";
    }
    if (!loanAmount) {
      newErrors.loanAmount = "Loan amount is required.";
    } else if (typeof loanAmount === "number" && loanAmount < 2000) {
      newErrors.loanAmount = "Loan amount must be at least $2000.";
    }
    if (!loanTerm) {
      newErrors.loanTerm = "Loan term is required.";
    } else if (typeof loanTerm === "number" && (loanTerm < 1 || loanTerm > 7)) {
      newErrors.loanTerm = "Loan term must be between 1 and 7 years.";
    }

    return newErrors;
  };

  // Reset form + errors
  const resetForm = () => {
    setFirstName("");
    setEmailAddress("");
    setLoanAmount("");
    setLoanTerm("");
    setErrors({});
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    const newErrors = validateAllFields();

    if (Object.keys(newErrors).length > 0) {
      setErrors(newErrors);
      return;
    }

    alert("Form submitted successfully!");
    resetForm(); // ✅ clear fields + errors after success
  };

  return (
    <form onSubmit={handleSubmit}>
      {/* First Name */}
      <div>
        <input
          type="text"
          placeholder="First Name"
          value={firstName}
          onChange={(e) => setFirstName(e.target.value)}
          onBlur={() => setErrors(validateAllFields())}
        />
        {errors.firstName && <p style={{ color: "red" }}>{errors.firstName}</p>}
      </div>

      {/* Email */}
      <div>
        <input
          type="email"
          placeholder="Email Address"
          value={emailAddress}
          onChange={(e) => setEmailAddress(e.target.value)}
          onBlur={() => setErrors(validateAllFields())}
        />
        {errors.emailAddress && (
          <p style={{ color: "red" }}>{errors.emailAddress}</p>
        )}
      </div>

      {/* Loan Amount */}
      <div>
        <input
          type="number"
          placeholder="Loan Amount"
          value={loanAmount}
          onChange={(e) =>
            setLoanAmount(e.target.value === "" ? "" : Number(e.target.value))
          }
          onBlur={() => setErrors(validateAllFields())}
        />
        {errors.loanAmount && (
          <p style={{ color: "red" }}>{errors.loanAmount}</p>
        )}
      </div>

      {/* Loan Term */}
      <div>
        <input
          type="number"
          placeholder="Loan Term (1-7 years)"
          value={loanTerm}
          onChange={(e) =>
            setLoanTerm(e.target.value === "" ? "" : Number(e.target.value))
          }
          onBlur={() => setErrors(validateAllFields())}
        />
        {errors.loanTerm && <p style={{ color: "red" }}>{errors.loanTerm}</p>}
      </div>

      <button type="submit" disabled={Object.keys(errors).length > 0}>
        Submit
      </button>
    </form>
  );
}

export default LoanOfferComparison;
