/**
 * Assignment Requirements Covered:
 * 1. Create a frontend form with fields:
 *    - First Name
 *    - Email Address
 *    - Loan Amount (minimum $2000)
 *    - Loan Term (1–7 years)
 *
 * 2. Implement real-time form validation:
 *    - First name required
 *    - Email required and must be valid format
 *    - Loan amount required and must be ≥ $2000
 *    - Loan term required and must be between 1–7 years
 *
 * 3. Show inline error messages when inputs are invalid.
 *
 * 4. Disable Submit button until all fields are valid.
 *
 * 5. On Submit:
 *    - Send form data to backend endpoint (/api/offers) via POST request
 *    - Receive loan offers from backend
 *    - Display loan offers in a list with lender name, interest rate, fees, and calculated monthly repayment
 *
 * 6. Reset form fields after successful submission.
 */

import React, { useState } from "react";

// Type for backend loan offer response
type LoanOffer = {
  lenderName: string;
  monthlyRepayment: number;
  interestRate: number;
  fees: string;
};

function LoanOfferComparison() {
  // ---------------- Form State ----------------
  const [firstName, setFirstName] = useState(""); // First name input
  const [emailAddress, setEmailAddress] = useState(""); // Email input
  const [loanAmount, setLoanAmount] = useState<number | "">(""); // Loan amount input
  const [loanTerm, setLoanTerm] = useState<number | "">(""); // Loan term input

  const [errors, setErrors] = useState<{ [key: string]: string }>({}); // Tracks validation errors
  const [touched, setTouched] = useState<{ [key: string]: boolean }>({}); // Tracks fields that were touched
  const [offers, setOffers] = useState<LoanOffer[]>([]); // Stores loan offers returned from backend

  // ---------------- Validation Functions ----------------
  // Validate all fields on submit
  /*
  const validateAllFields = () => {
    const newErrors: { [key: string]: string } = {};
    if (!firstName.trim()) newErrors.firstName = "First name is required."; // Requirement 2
    if (!emailAddress) {
      newErrors.emailAddress = "Email address is required."; // Requirement 2
    } else if (!/\S+@\S+\.\S+/.test(emailAddress)) {
      newErrors.emailAddress = "Enter a valid email."; // Requirement 2
    }
    if (!loanAmount) {
      newErrors.loanAmount = "Loan amount is required."; // Requirement 2
    } else if (typeof loanAmount === "number" && loanAmount < 2000) {
      newErrors.loanAmount = "Loan amount must be at least $2000."; // Requirement 1 & 2
    }
    if (!loanTerm) {
      newErrors.loanTerm = "Loan term is required."; // Requirement 2
    } else if (typeof loanTerm === "number" && (loanTerm < 1 || loanTerm > 7)) {
      newErrors.loanTerm = "Loan term must be between 1 and 7 years."; // Requirement 1 & 2
    }
    return newErrors;
  };
  */

  // Validate a single field on blur/change
  const validateField = (field: string, value: any) => {
    let error = "";
    if (field === "firstName" && !value.trim())
      error = "First name is required.";
    if (field === "emailAddress") {
      if (!value) error = "Email address is required.";
      else if (!/\S+@\S+\.\S+/.test(value)) error = "Enter a valid email.";
    }
    if (field === "loanAmount") {
      if (!value) error = "Loan amount is required.";
      else if (typeof value === "number" && value < 2000)
        error = "Loan amount must be at least $2000.";
    }
    if (field === "loanTerm") {
      if (!value) error = "Loan term is required.";
      else if (typeof value === "number" && (value < 1 || value > 7))
        error = "Loan term must be between 1 and 7 years.";
    }
    return error;
  };

  // ---------------- Handlers ----------------
  // Handle field blur (mark touched & validate)
  const handleBlur = (field: string, value: any) => {
    setTouched({ ...touched, [field]: true }); // Track that user interacted with field
    setErrors({ ...errors, [field]: validateField(field, value) }); // Show inline error
  };

  // Handle field change (update state & real-time validation)
  const handleChange = (field: string, value: any) => {
    if (field === "firstName") setFirstName(value);
    if (field === "emailAddress") setEmailAddress(value);
    if (field === "loanAmount")
      setLoanAmount(value === "" ? "" : Number(value));
    if (field === "loanTerm") setLoanTerm(value === "" ? "" : Number(value));

    // Always validate (not just when touched)
    setErrors({ ...errors, [field]: validateField(field, value) });

    // Validate in real-time if user already touched the field
    if (touched[field]) {
      setErrors({ ...errors, [field]: validateField(field, value) }); // Inline error
    }
  };

  // Reset form after successful submission
  const resetForm = () => {
    setFirstName("");
    setEmailAddress("");
    setLoanAmount("");
    setLoanTerm("");
    setErrors({});
    setTouched({});
  };

  // Handle form submit
  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    if (!isFormValid) return;

    try {
      // ---------------- API Call to Backend ----------------
      const response = await fetch("http://localhost:5000/api/offers", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          firstName,
          emailAddress,
          loanAmount,
          loanTerm,
        }),
      });

      if (!response.ok) throw new Error("Failed to fetch offers");

      const data: LoanOffer[] = await response.json();
      setOffers(data); // Display offers (Requirement 5)

      resetForm(); // Reset form (Requirement 6)
    } catch (error) {
      console.error(error);
      alert("Something went wrong, please try again."); // Error handling
    }
  };

  // Disable submit until all fields are valid (Requirement 4)
  const isFormValid =
    Object.values(errors).every((err) => !err) &&
    firstName &&
    emailAddress &&
    loanAmount &&
    loanTerm;

  // ---------------- UI ----------------
  return (
    <div>
      <header>
        <h1>LOAN OFFER COMPARISON TOOL</h1>
      </header>
      <main>
        <form onSubmit={handleSubmit}>
          {/* First Name Input */}
          <div>
            <label>FIRST NAME*</label>
            <input
              type="text"
              value={firstName}
              onChange={(e) => handleChange("firstName", e.target.value)}
              onBlur={(e) => handleBlur("firstName", e.target.value)}
            />
            {touched.firstName && errors.firstName && (
              <span className="errorText">{errors.firstName}</span> // Inline error (Requirement 3)
            )}
          </div>

          {/* Email Address Input */}
          <div>
            <label>EMAIL ADDRESS*</label>
            <input
              type="email"
              value={emailAddress}
              onChange={(e) => handleChange("emailAddress", e.target.value)}
              onBlur={(e) => handleBlur("emailAddress", e.target.value)}
            />
            {touched.emailAddress && errors.emailAddress && (
              <span className="errorText">{errors.emailAddress}</span> // Inline error (Requirement 3)
            )}
          </div>

          {/* Loan Amount Input */}
          <div>
            <label>LOAN AMOUNT ($)*</label>
            <input
              type="number"
              placeholder="At least $2000"
              value={loanAmount}
              onChange={(e) => handleChange("loanAmount", e.target.value)}
              onBlur={(e) => handleBlur("loanAmount", e.target.value)}
            />
            {touched.loanAmount && errors.loanAmount && (
              <span className="errorText">{errors.loanAmount}</span> // Inline error (Requirement 3)
            )}
          </div>

          {/* Loan Term Input */}
          <div>
            <label>LOAN TERM*</label>
            <input
              type="number"
              placeholder="1-7 years"
              value={loanTerm}
              onChange={(e) => handleChange("loanTerm", e.target.value)}
              onBlur={(e) => handleBlur("loanTerm", e.target.value)}
            />
            {touched.loanTerm && errors.loanTerm && (
              <span className="errorText">{errors.loanTerm}</span> // Inline error (Requirement 3)
            )}
          </div>

          {/* Submit Button */}
          <button
            className="submitButton"
            type="submit"
            disabled={!isFormValid}
          >
            Submit
          </button>
        </form>

        {/* Display Loan Offers (Requirement 5) */}
        {offers.length > 0 && (
          <div className="offersContainer">
            <h3>LOAN OFFERS</h3>
            <table className="offersTable">
              <thead>
                <tr>
                  <th>Lender</th>
                  <th>Monthly Repayment</th>
                  <th>Interest Rate</th>
                  <th>Fees</th>
                </tr>
              </thead>
              <tbody>
                {offers.map((offer, idx) => (
                  <tr key={idx}>
                    <td>{offer.lenderName}</td>
                    <td>${offer.monthlyRepayment.toFixed(2)}</td>
                    <td>{offer.interestRate}%</td>
                    <td>{offer.fees}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}
      </main>
    </div>
  );
}

export default LoanOfferComparison;
