import React, { useState } from "react";

function LoanOfferComparison() {
  //form state
  const [firstName, setFirstName] = useState("");
  const [emailAddress, setEmailAddress] = useState("");
  const [loanAmount, setLoanAmount] = useState<number | "">("");
  const [loanTerm, setLoanTerm] = useState<number | "">("");

  //handle submit
  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();

    //simple validation
    if (!firstName || !emailAddress || !loanAmount || !loanTerm) {
      alert("Please fill in all fields.");
      return;
    }

    if (loanAmount < 2000) {
      alert("Loan amount must be at least $2,000.");
      return;
    }

    if (loanTerm < 1 || loanTerm > 7) {
      alert("Loan term must be between 1 and 7 years.");
      return;
    }

    console.log({ firstName, emailAddress, loanAmount, loanTerm });
    alert("Form submitted! (Check console for values");
  };

  return (
    <div>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          placeholder="First Name"
          value={firstName}
          onChange={(e) => setFirstName(e.target.value)}
          required
        />
        <br />

        <input
          type="email"
          placeholder="Email Address"
          value={emailAddress}
          onChange={(e) => setEmailAddress(e.target.value)}
          required
        />
        <br />

        <input
          type="number"
          placeholder="Loan Amount"
          value={loanAmount}
          onChange={(e) => setLoanAmount(Number(e.target.value))}
          required
          min={2000}
        />
        <br />

        <input
          type="number"
          placeholder="Loan Term (1-7 Years)"
          value={loanTerm}
          onChange={(e) => setLoanTerm(Number(e.target.value))}
          required
          min={1}
          max={7}
        />
        <br />

        <button type="submit">Submit</button>
      </form>
    </div>
  );
}

export default LoanOfferComparison;
